mapper.select:
select distinct u.user_name from users u
join homework h on (u.user_id = h.user_id_leader)
where h.user_id_leader = #{user_id_leader, jdbcType=VARCHAR}

mapper.update:
update homework
set homework_content_learner = #{homework_content_learner, jdbcType=VARCHAR}
, homework_subdate = sysdate
where user_id_learner = #{user_id_learner, jdbcType=VARCHAR}
